  document.querySelector('.p4').textContent =  'e-mail:';
  document.querySelector('.p4_4').textContent = `${formObject.email}`;

  document.querySelector('.p5').textContent =  'phone:';
  document.querySelector('.p5_5').textContent = `${formObject.phone}`;

  document.querySelector('.p6').textContent =  'city:';
  document.querySelector('.p6_6').textContent = `${formObject.city}`;

  document.querySelector('.p7').textContent =  'payment:';
  document.querySelector('.p7_7').textContent = `${formObject.pay_method}`;

